package uz.example.dagger_2_android3.core.baseimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport dagger.android.support.DaggerFragmentabstract class BaseFragment : DaggerFragment() {    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {        return createView()    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        onViewCreated()    }    abstract fun createView(): View    abstract fun onViewCreated()}