package uz.example.dagger_2_android3.core.di.componentimport android.app.Applicationimport dagger.BindsInstanceimport dagger.Componentimport dagger.android.AndroidInjectionModuleimport dagger.android.AndroidInjectorimport uz.example.dagger_2_android3.core.app.Appimport uz.example.dagger_2_android3.core.di.module.context.AppModuleimport uz.example.dagger_2_android3.core.di.module.network.NetworkModuleimport uz.example.dagger_2_android3.core.di.module.network.ServiceModuleimport uz.example.dagger_2_android3.core.di.module.repo.RepositoriesModuleimport uz.example.dagger_2_android3.core.di.module.ui.ActivityBuilderModuleimport javax.inject.Singleton@Singleton@Component(    modules = [        AndroidInjectionModule::class,        AppModule::class,        NetworkModule::class,        ServiceModule::class,        RepositoriesModule::class,        ActivityBuilderModule::class    ])interface AppComponent : AndroidInjector<App> {    @Component.Builder    interface Builder {        @BindsInstance        fun application(application: Application):Builder        fun build():AppComponent    }}