package uz.example.dagger_2_android3.core.di.module.networkimport dagger.Moduleimport dagger.Providesimport retrofit2.Retrofitimport uz.example.dagger_2_android3.core.network.NewsApiimport javax.inject.Singleton@Moduleobject ServiceModule {    @Provides    @Singleton    fun provideNewsApi(        retrofit: Retrofit    ):NewsApi {        return retrofit.create(NewsApi::class.java)    }}