package uz.example.dagger_2_android3.core.di.module.ui.main.homeimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport dagger.Bindsimport dagger.Moduleimport dagger.multibindings.IntoMapimport uz.example.dagger_2_android3.core.di.annotation.ViewModelKeyimport uz.example.dagger_2_android3.core.utils.ViewModelProviderFactoryimport uz.example.dagger_2_android3.ui.home.HomeVM@Moduleinterface HomeVMM {    @Binds    fun bindViewModelProviderFactory(factory: ViewModelProviderFactory):ViewModelProvider.Factory    @Binds    @IntoMap    @ViewModelKey(HomeVM::class)    fun bindMainViewModel(        homeVM: HomeVM    ):ViewModel}