package uz.example.dagger_2_android3.ui.homeimport android.view.Viewimport androidx.fragment.app.viewModelsimport uz.example.dagger_2_android3.core.base.BaseFragmentimport uz.example.dagger_2_android3.core.utils.ViewModelProviderFactoryimport uz.example.dagger_2_android3.databinding.FragmentHomeBindingimport javax.inject.Injectclass HomeFragment : BaseFragment() {    @Inject    lateinit var viewModelProvider: ViewModelProviderFactory    private val viewModel: HomeVM by viewModels { viewModelProvider }    private val binding by lazy {        FragmentHomeBinding.inflate(layoutInflater)    }    override fun onViewCreated() {        viewModel.topHeadlineResponse.observe(viewLifecycleOwner) {            binding.resultShop.text = it.toString()        }        viewModel.errorResponse.observe(viewLifecycleOwner) {            binding.resultShop.text = it.toString()        }        viewModel.loadTopHeadline()    }    override fun createView(): View {        return binding.root    }}